 (function ($) {
	'use strict';

	$(document).ready(function () {
		var Contact = {
			init: function() {
				this.getUrl();
			},
			getUrl: function() {
				$.getJSON('https://big-andy.co.uk/wp-json/wp/v2/posts', function(data) {
					var dataLength = data.length,
						i = 0,
						content = '<ul class="list--plain">';

					for (i; i < dataLength; i++) {
						console.log(data[i]);

						var link = data[i].link,
							title = data[i].title.rendered,
							excerpt = data[i].excerpt.rendered;

						content += '<li><a href="' + link + '" >' + title + '</a>' + excerpt + '</li>';
					}
					content += '</ul>';
					$('header').after(content);
				});
			},
		};
		Contact.init();
	});
 }(jQuery));
